"package %PACKAGE%;
import ghidra.util.TestUtilities;
import junit.framework.Test;

/**
 * Generic test suite for single package.
 */
public class %CLASSNAME% {
	public static Test suite() {
		%CLASSNAME% testAll = new %CLASSNAME%();
		return TestUtilities.getPkgTestSuite(testAll.getClass().getPackage().getName());
	}
}
"
